def compute_Last_Digit(A, B):
    # Factorial of A divides factorial of B means B! / A! = (B)*(B-1)*...*(A+1)
    # Since we only need the last digit, we can simplify the calculation by modulo 10
    if A == B or B < A:
        return 1  # Since the division would be 1 or undefined (B<A), the last digit would be equal to 1
    last_digit = 1
    for i in range(A + 1, B + 1):
        last_digit = (last_digit * i) % 10
        if last_digit == 0:
            break
    return last_digit

