        if n == 0 or n == 1:
            return 1
        else:
            return n * factorial(n-1)

    def last_digit(num):
        return num % 10

    result = min(A, B)
    last_A = last_digit(factorial(A))
    last_B = last_digit(factorial(B))

    if A == B:
        return last_A
    else:
        return last_digit(factorial(result))

