def eulerian_num(n, m):
    # Create a table to store values of the Eulerian numbers
    dp = [[0 for _ in range(n)] for _ in range(n)]

    # Initialize the base cases for the Eulerian numbers
    for i in range(n):
        dp[i][0] = 1

    # Fill in the table using a bottom-up approach
    for i in range(1, n):
        for j in range(1, i):
            dp[i][j] = (i-j) * dp[i-1][j-1] + (j+1) * dp[i-1][j]

    # Return the result from the table
    return dp[n-1][m-1]

