def magic_square_test(my_matrix):
    n = len(my_matrix)
    magic_const = n * (n**2 + 1) / 2

    sum_row = [0] * n
    sum_col = [0] * n
    sum_diag = [0, 0]

    for i in range(n):
        for j in range(n):
            sum_row[i] += my_matrix[i][j]
            sum_col[j] += my_matrix[i][j]
            if i == j:
                sum_diag[0] += my_matrix[i][j]
            if i + j == n - 1:
                sum_diag[1] += my_matrix[i][j]

    if (any(x != magic_const for x in sum_row) or any(x != magic_const for x in sum_col) or sum_diag[0] != magic_const or sum_diag[1] != magic_const):
        return False
    return True

